package com.java.Level_1;

public class Escape_characters {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * ava escape sequence:-
		 *	• \t Insert a tab in the text at this point.
		 *	• \b Insert a backspace in the text at this point.
		 *	• \n Insert a newline in the text at this point.
		 *	• \r Insert a carriage return in the text at this point.
		 *	• \f Insert a formfeed in the text at this point.
		 *	• \' Insert a single quote character in the text at this point.
		 *	• \" Insert a double quote character in the text at this point.
		 *	• \\ Insert a backslash character in the text at this point.
		 */
		
		System.out.println("Tester\nGroup");
		System.out.println("Tester\tGroup");
		System.out.println("\bTester\bGroup");
		System.out.println("Tester\rGroup");
		System.out.println("Tester\fGroup");
		System.out.println("Tester\'Group");
		System.out.println("Tester\"Group");
		System.out.println("Tester\\Group");
		System.out.println("Tester\\\\Group");
		System.out.println("Tester\bbGroup");
//		System.out.println("Tester\ff\\Group");
		
		
		
		
		
		
		
		
		
	}

}
